mapper.insert:
insert into system_config (supplier_id, default_income_ratio, default_pay_ratio,default_max_effect_con,
						   default_max_effect_range, update_time, update_by
)
values (#{supplier.id,jdbcType=BIGINT}, #{defaultIncomeRatio,jdbcType=DOUBLE},#{defaultPayRatio,jdbcType=DOUBLE}, #{defaultMaxEffectCon,jdbcType=DOUBLE},
		#{defaultMaxEffectRange,jdbcType=INTEGER}, #{updateTime,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=BIGINT}
	   )

mapper.select:
select ,
from system_config sc left join supplier sup on sc.supplier_id = sup.id where
sc.supplier_id = #{supplierId,jdbcType=BIGINT}
sc.supplier_id is null
sc.supplier_id = #{id,jdbcType=BIGINT}
